"use strict";var app={colors:["rgb(205,35,39)","rgb(0,175,220)","rgb(242,112,35)","rgb(191,205,63)","rgb(119,73,0)","rgb(152,0,212)"],items:new Array(15),init:function(){app.container=document.createElement("div");app.container.className="container";document.body.appendChild(app.container);for(var a=0;a<app.items.length;a++){app.add(app.items[a])}},add:function(){var a=document.createElement("div");a.classList.add("hidden");app.container.appendChild(a);var e=Math.round(Math.random()*1e3);window.setTimeout(app.show,e,a)},show:function(a){a.classList.remove("hidden");app.animate(a)},animate:function(a){var e=Math.ceil(Math.random()*15)*1e3;window.setTimeout(function(a){a.classList.toggle("animate");app.animate(a)},e,a)}};document.addEventListener("DOMContentLoaded",app.init);