"use strict";var app={items:new Array(15),init:function(){app.container=document.createElement("div");app.container.className="container";document.body.appendChild(app.container);for(var a=0;a<app.items.length;a++){app.add(app.items[a])}},add:function(){var a=document.createElement("div");a.style.background="#"+Math.random().toString(16).substr(2,6);a.classList.add("hidden");app.container.appendChild(a);var t=Math.round(Math.random()*1e3);window.setTimeout(app.show,t,a)},show:function(a){a.classList.remove("hidden");app.animate(a)},animate:function(a){var t=Math.ceil(Math.random()*15)*1e3;window.setTimeout(function(a){a.classList.toggle("animate");a.style.background="#"+Math.random().toString(16).substr(2,6);app.animate(a)},t,a)}};document.addEventListener("DOMContentLoaded",app.init);