"use strict";var app={colors:["rgb(205,35,39)","rgb(0,175,220)","rgb(242,112,35)","rgb(191,205,63)","rgb(119,73,0)","rgb(152,0,212)"],items:new Array(15),init:function(){app.container=document.createElement("div");app.container.className="container";document.body.appendChild(app.container);for(var e=0;e<app.items.length;e++){app.add(app.items[e])}},add:function(){var e=document.createElement("div");e.classList.add("hidden");e.addEventListener("mouseover",app.setColor,false);e.addEventListener("mouseout",app.setColor,false);app.container.appendChild(e);var a=Math.round(Math.random()*1e3);window.setTimeout(app.show,a,e)},show:function(e){e.classList.remove("hidden");app.animate(e)},animate:function(e){var a=Math.ceil(Math.random()*15)*1e3;window.setTimeout(function(e){e.classList.toggle("animate");app.animate(e)},a,e)},setColor:function(e){var a=e.target;if(a.style.background){a.style.background=""}else{a.style.background=app.getRandomColor()}},getRandomColor:function(){return app.colors[Math.floor(Math.random()*app.colors.length)]}};document.addEventListener("DOMContentLoaded",app.init);